import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import '../styles/Projects.scss';

const projects = [
  {
    id: 1,
    title: 'ETL Platform Design',
    description: 'Redesigned complex data transformation workflows into intuitive interfaces',
    image: '/projects/etl-platform.jpg',
    category: 'Enterprise',
    year: '2023'
  },
  {
    id: 2,
    title: 'B2B SaaS Dashboard',
    description: 'Created a unified dashboard for managing multiple business processes',
    image: '/projects/saas-dashboard.jpg',
    category: 'SaaS',
    year: '2023'
  },
  {
    id: 3,
    title: 'Data Visualization Tool',
    description: 'Developed interactive data visualization components for complex datasets',
    image: '/projects/data-viz.jpg',
    category: 'Enterprise',
    year: '2022'
  }
  // Add more projects as needed
];

const Projects = () => {
  return (
    <motion.div 
      className="projects"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="projects-header">
        <motion.h1
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          Selected Works
        </motion.h1>
        <motion.p
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.1 }}
          className="subtitle"
        >
          A collection of projects I've worked on
        </motion.p>
      </div>

      <motion.div 
        className="projects-grid"
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        {projects.map((project, index) => (
          <Link to={`/projects/${project.id}`} key={project.id}>
            <motion.div 
              className="project-card"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ y: -10 }}
            >
              <div className="project-image">
                <img src={project.image} alt={project.title} />
                <div className="project-overlay">
                  <span className="view-project">View Project</span>
                </div>
              </div>
              <div className="project-info">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className="project-meta">
                  <span className="category">{project.category}</span>
                  <span className="year">{project.year}</span>
                </div>
              </div>
            </motion.div>
          </Link>
        ))}
      </motion.div>
    </motion.div>
  );
};

export default Projects;
