import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { FiDownload, FiLinkedin } from 'react-icons/fi';
import '../styles/Home.scss';

const Home = () => {
  const handleDownloadResume = () => {
    // Add your resume download logic here
    console.log('Downloading resume...');
  };

  return (
    <motion.div 
      className="home"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <section className="hero">
        <div className="hero-content">
          <motion.h1
            initial={{ y: 30, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ 
              duration: 0.8,
              ease: "easeOut"
            }}
          >
            Hi, I'm <motion.span 
              className="highlight"
              whileHover={{ 
                scale: 1.05,
                transition: { duration: 0.2 }
              }}
            >
              Your Name
            </motion.span>
          </motion.h1>
          <motion.p
            className="subtitle"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ 
              duration: 0.8,
              delay: 0.2,
              ease: "easeOut"
            }}
          >
            Principal Product Designer crafting intuitive digital experiences
          </motion.p>
          <motion.div 
            className="cta-buttons"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ 
              duration: 0.8,
              delay: 0.4,
              ease: "easeOut"
            }}
          >
            <Link to="/projects">
              <motion.button
                className="outline-btn primary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                View Projects
              </motion.button>
            </Link>
            <motion.button
              className="outline-btn secondary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleDownloadResume}
            >
              Download Resume <FiDownload className="icon" />
            </motion.button>
            <motion.a
              href="https://linkedin.com/in/yourprofile"
              target="_blank"
              rel="noopener noreferrer"
              className="outline-btn linkedin"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiLinkedin className="icon" /> LinkedIn
            </motion.a>
          </motion.div>
        </div>
      </section>
    </motion.div>
  );
};

export default Home;
